import React, { useState, useEffect } from "react";
import { BrowserRouter } from "react-router-dom";
import "./App.css";
import Home from "./Home";
import SnackOrBoozeApi from "./Api";
import NavBar from "./NavBar";
import { Route, Switch } from "react-router-dom";
import Menu from "./FoodMenu";
import Snack from "./FoodItem";

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    async function getAll() {
      let items = await SnackOrBoozeApi.getAll();
      setMenuItems(items.data);
      setIsLoading(false);
      console.log("ok")
    }
    getAll();

  }, []);
  console.log(menuItems)



  if (isLoading) {
    return <p>Loading &hellip;</p>;
  }

  return (
    <div className="App">
      <BrowserRouter>
        <NavBar />
        <main>
          <Switch>
            <Route exact path="/">
              <Home snacks={menuItems.snacks} />
            </Route>
            <Route exact path="/snacks">
              <Menu snacks={menuItems.snacks} title="Snacks" />
            </Route>
            <Route path="/snacks/:id">
              <Snack items={menuItems.snacks} cantFind="/snacks" />
            </Route>

            <Route path="/drinks">
              <Menu snacks = {menuItems.drinks} title="Drinks" />
            </Route>
            <Route path="/drinks/:id">
              <Snack items = {menuItems.drinks} cantFind="/drinks" />
            </Route>

            <Route>
              <p>Hmmm. I can't seem to find what you want.</p>
            </Route>
          </Switch>
        </main>
      </BrowserRouter>
    </div>
  );
}

export default App;
