import React, { useState } from "react";
import { Card, CardBody, CardTitle } from "reactstrap";

function NewItemForm({title}) {
    const [newItem,setNewItem] = useState({id:'', name:'',description:'', recipe:'',serve:''})

    const {id, name, description, recipe, server} = newItem

    return (
    <section className="col-md-8">
      <Card>
        <CardBody className="text-center">
            <h3>New {title}</h3>
          <form>
            <label htmlFor="id">id:</label>
            <input type="text" id="id" name="id" placeholder="ex. 'creation-name'" value = {id}></input>

            <label htmlFor="name">name:</label>
            <input type="text" id="name" name="name" placeholder="ex. 'apple'" value = {name}></input>

            <label htmlFor="description">description:</label>
            <input type="text" id="description" name="description" placeholder="ex. 'very tasty'" value = {description}></input>

            <label htmlFor="recipe">id:</label>
            <input type="text" id="recipe" name="recipe" placeholder="ex. 'one olive'" value = {recipe}></input>

            <label htmlFor="server">server:</label>
            <input type="text" id="server" name="server" placeholder="ex. 'creation-name'" value = {server}></input>

          </form>
        </CardBody>
      </Card>
    </section>
  );
}

export default NewItemForm;
